import React, { useState } from "react";
import Gellary from "./Gellary";
import classes from "./GalleryReact.module.css";

const GellaryReact = () => {
  const [items, setItems] = useState(Gellary);

  const clickHandler = (categ) => {
      const updated = Gellary.filter((event) => {
        return event.category === categ ;
      })

      setItems (updated)
  };

  return (
    <div>
      <header>
        <h1 className={classes.h1}>Flower Gallery</h1>
        <div className={classes.tabs}>
          <div className={classes.menutabs}>
            <button className={classes.button} onClick={() => clickHandler('rose')}>Rose</button>
            <button className={classes.button} onClick={() => clickHandler('daisy')}>Daisy</button>
            <button className={classes.button} onClick={() => clickHandler('orchid')}>Orchid</button>
            <button className={classes.button} onClick={() => clickHandler('sunflower')}>Sun Flower</button>
            <button className={classes.button} onClick={() => setItems(Gellary)}>All</button>
          </div>
        </div>
        <div className={classes.search}>
          <input type="text" placeholder="search any flower" auto/>
          <button type="submit" >search</button>
        </div>
      </header>
      <main>
        <div className={classes.mainitems}>
          {items.map((event) => {
            const { id, image, name, category } = event;

            return (
              <div className="">
                <img src={event.image} alt={event.name} className={classes.images} />
              </div>
            );
          })}
          <div className={classes.row}>
            <div className={classes.col}>
            </div>
          </div>
        </div>
      </main>
    </div>
  );
};

export default GellaryReact;
